mapper.select:
SELECT cs.id, cs.student_name, cs.create_time, ct.course_name, ct.teacher_name FROM course_student_rel cs LEFT JOIN course_teacher_rel ct ON cs.rel_id = ct.id WHERE cs.`status` = 1 AND cs.student_id = #{studentId,jdbcType=INTEGER}
AND ct.course_name like '%${courseName}%'
AND ct.teacher_name like '%${teacherName}%'

SELECT count(*) from ( SELECT cs.id, cs.student_name, cs.create_time, ct.course_name, ct.teacher_name FROM course_student_rel cs LEFT JOIN course_teacher_rel ct ON cs.rel_id = ct.id WHERE cs.`status` = 1 AND cs.student_id = #{studentId,jdbcType=INTEGER}
AND ct.course_name like '%${courseName}%'
AND ct.teacher_name like '%${teacherName}%'
)table1

SELECT count(*) from ( SELECT cs.id, cs.student_name, cs.create_time, ct.course_name, ct.teacher_name FROM course_student_rel cs LEFT JOIN course_teacher_rel ct ON cs.rel_id = ct.id WHERE cs.`status` = 1 AND ct.teacher_id = #{teacherId,jdbcType=INTEGER}
AND cs.student_name like '%${studentName}%'
)table1
